        this.tiles.add("...................................................................................................................................................................................................");
        this.tiles.add("...................................................................................................................................................................................................");
        this.tiles.add("...................................................................................................................................................................................................");
        this.tiles.add("d..........p.........................................................b..........................................e........................b.........................................................");
        this.tiles.add("...................................................................................................................................................................................................");
        this.tiles.add("...................................................................................................................................................................................................");
        this.tiles.add("...................................................................................................................................................................................................");
        this.tiles.add(".......................................................................................................................................g......................................l....................");
        this.tiles.add(".........................................................c...............................4444444444444444.........................444444444444.....................................................");
        this.tiles.add("..................................c..................................................4444.........................................444..............................................................");
        this.tiles.add(".................................222.....................g...........................4444.c.......................g...............444.c.............g..............................................");
        this.tiles.add("222222222222222222222222...222222222222222222222222222222222222222222222......111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111111");
        this.tiles.add("...................................................................................................................................................................................................");
        this.tiles.add("...................................................................................................................................................................................................");
        this.tiles.add("...................................................................................................................................................................................................");
        this.tiles.add("...................................................................................................................................................................................................");
        this.tiles.add("...................................................................................................................................................................................................");
        backgroundDataList = new ArrayList<>();
        // note that speeds less than 2 cause problems
        //startY and endY determine where on the coordinate map above the background is placed
        this.backgroundDataList.add(new BackgroundData("forest_background", true, -1, -8, 18, 10, 15 ));
        this.backgroundDataList.add(new BackgroundData("grass", true, 1, 11, 18, 24, 4 ));
