package com.example.babblesgame;

import java.util.ArrayList;

public class LevelWater extends LevelData{
    LevelWater() {
        tiles = new ArrayList<>();
        //remember at least one c, p and d must be in each level
        // Also starting the player off too low minimizes how high one can jump at so keep it at the top
        this.tiles.add("..............................................................................................................................4444.................................................................");
        this.tiles.add("..............................................................................................................................4444.................................................................");
        this.tiles.add("..............................................................................................................................4444.................................................................");
        this.tiles.add("d..........p.................................................................................e................................4444.................................................................");
        this.tiles.add("...........................................................................................44444.................................44.c.................................................a............");
        this.tiles.add("...............................................................................................................................c.44444444..........................................................");
        this.tiles.add(".............................................................................................................................u4444444444444444444444444............................................");
        this.tiles.add("...............................................................................................................................................44444444.......................................l....");
        this.tiles.add("..........444444...............a...........44444444444444.......................c....................................4444......................444444444444444444444...............................");
        this.tiles.add("..........4444444444.......................4.......................................................4444444...........4444..........................................................................");
        this.tiles.add("..........44444444444444.................u.4.c...............................u....u................4444444...........4444..........................................................................");
        this.tiles.add("4444444444444444444444444444444444444444444444444444444444......44444444444444444444444444444......444444444444444444444444444444444444444444444444444444444444444444444444444444444444444444444444");
        this.tiles.add("...................................................................................................................................................................................................");
        this.tiles.add("...................................................................................................................................................................................................");
        this.tiles.add("...................................................................................................................................................................................................");
        this.tiles.add("...................................................................................................................................................................................................");
        this.tiles.add("...................................................................................................................................................................................................");
        backgroundDataList = new ArrayList<>();
        // note that speeds less than 2 cause problems
        //startY and endY determine where on the coordinate map above the background is placed
        this.backgroundDataList.add(new BackgroundData("water_background", true, -1, -8, 18, 10, 15 ));
        this.backgroundDataList.add(new BackgroundData("seaweed", true, 1, 11, 18, 24, 4 ));
    }
}